import React, { useRef, useState } from "react";
import "./App.css";
import { Resizable } from "re-resizable";
import axios from "axios";

function App() {
  const resizableContainerRef = useRef(null);
  const [formData, setFormData] = useState({
    name: "",
    description: ""
  });

  const handleResizeStart = () => {
    resizableContainerRef.current.classList.add("resizing");
  };

  const handleResizeStop = () => {
    resizableContainerRef.current.classList.remove("resizing");
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleAddData = async () => {
    try {
      const response = await axios.post("http://localhost:3001/api/data", formData);
      console.log(response.data); 
      setFormData({
        name: "",
        description: ""
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div className="App">
      <div className="container" ref={resizableContainerRef}>
        <div className="row">
          <Resizable
            className="resizeable"
            defaultSize={{ width: 200, height: 200 }}
            onResizeStart={handleResizeStart}
            onResizeStop={handleResizeStop}
            enable={{
              top: true,
              right: true,
              bottom: true,
              left: true,
              topRight: true,
              bottomRight: true,
              bottomLeft: true,
              topLeft: true,
            }}
          >
            <div className="resizable-content" style={{ backgroundColor: "lightblue" }}>
              <h2>Resizable Div 1</h2>
              {/* Form for adding new data */}
              <form>
                <input
                  type="text"
                  name="name"
                  placeholder="Name"
                  value={formData.name}
                  onChange={handleInputChange}
                />
                <input
                  type="text"
                  name="description"
                  placeholder="Description"
                  value={formData.description}
                  onChange={handleInputChange}
                />
                <button type="button" onClick={handleAddData}>Add</button>
              </form>
            </div>
          </Resizable>
        </div>
        <div className="row">
          <Resizable
            className="resizeable"
            defaultSize={{ width: 200, height: 200 }}
            onResizeStart={handleResizeStart}
            onResizeStop={handleResizeStop}
            enable={{
              top: true,
              right: true,
              bottom: true,
              left: true,
              topRight: true,
              bottomRight: true,
              bottomLeft: true,
              topLeft: true,
            }}
          >
            <div className="resizable-content" style={{ backgroundColor: "lightgreen" }}>Resizable Div 2</div>
          </Resizable>
        </div>
        <div className="row">
          <Resizable
            className="resizeable"
            defaultSize={{ width: 200, height: 200 }}
            onResizeStart={handleResizeStart}
            onResizeStop={handleResizeStop}
            enable={{
              top: true,
              right: true,
              bottom: true,
              left: true,
              topRight: true,
              bottomRight: true,
              bottomLeft: true,
              topLeft: true,
            }}
          >
            <div className="resizable-content" style={{ backgroundColor: "lightcoral" }}>Resizable Div 3</div>
          </Resizable>
        </div>
      </div>
    </div>
  );
}

export default App;
